mapper.select:
select * from(select rownum as rnum,b.* from (select (select count(*) from quest_post_like where a.post_id = post_id and post_like='like') as post_like, (select count(*) from quest_post_like where a.post_id = post_id and post_like='dislike') as post_dislike, a.* from (select * from quest_post where
game_abb = #{game_abb}
game_abb like('%')
and
board_id = #{board_idList.value}
and board_id = #{board_id}
and
(user_id like '%'||#{text}||'%' or post_title like '%'||#{text}||'%' or post_content like '%'||#{text}||'%')
user_id like '%'||#{text}||'%'
post_title like '%'||#{text}||'%'
post_content like '%'||#{text}||'%'
(post_title like '%'||#{text}||'%' or post_content like '%'||#{text}||'%')
order by post_regdate desc ) a) bwhere post_like >= 2) where rnum between #{start} and #{end}

select a.*, (select count(*) from quest_post_like where a.post_id = post_id and post_like='like') as post_like,
		(select count(*) from quest_post_like where a.post_id = post_id and post_like='dislike') as post_dislike
		  from quest_post a where post_id = #{value}
